mapper.select:
SELECT am.row_id, am.epu_id, am.ammeter_name, am.ammeter_id, am.house_id, am.phase_remark, am.CREATE_TIME, am.CREATE_ID, am.UPDATE_TIME, am.UPDATE_ID, am.DEL_FLAG, am.district_id, am.address_id, am.channel_id, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_name, a.PROVINCE_NAME_CN AS epu_province_name, b.CITY_NAME_CN AS epu_city_name, c.CITY_NAME_CN AS epu_district_name FROM t_ammeter_info am LEFT JOIN t_epu_info epu ON am.epu_id = epu.row_id LEFT JOIN t_sy_provinces_info a ON epu.epu_province = a.PROVINCE_ID LEFT JOIN t_sy_city_info b ON epu.epu_city = b.CITY_CODE LEFT JOIN t_sy_city_info c ON epu.epu_district = c.CITY_CODE where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and epu.epu_province= #{epuProvince,jdbcType=VARCHAR}
and epu.epu_city= #{epuCity,jdbcType=VARCHAR}
and epu.epu_district= #{epuDistrict,jdbcType=VARCHAR}
and LOWER(am.ammeter_name) like LOWER(CONCAT("%",#{ammeterName,jdbcType=VARCHAR},"%"))
and ( LOWER(am.district_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.address_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.channel_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) )
order by am.CREATE_TIME desc

SELECT count(am.row_Id) FROM t_ammeter_info am LEFT JOIN t_epu_info epu ON am.epu_id = epu.row_id LEFT JOIN t_sy_provinces_info a ON epu.epu_province = a.PROVINCE_ID LEFT JOIN t_sy_city_info b ON epu.epu_city = b.CITY_CODE LEFT JOIN t_sy_city_info c ON epu.epu_district = c.CITY_CODE where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and epu.epu_province= #{epuProvince,jdbcType=VARCHAR}
and epu.epu_city= #{epuCity,jdbcType=VARCHAR}
and epu.epu_district= #{epuDistrict,jdbcType=VARCHAR}
and LOWER(am.ammeter_name) like LOWER(CONCAT("%",#{ammeterName,jdbcType=VARCHAR},"%"))
and ( LOWER(am.district_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.address_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) or LOWER(am.channel_id) like LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%")) )

SELECT am.row_id, am.epu_id, am.ammeter_name, am.ammeter_id, am.house_id, am.phase_remark, am.CREATE_TIME, am.CREATE_ID, am.UPDATE_TIME, am.UPDATE_ID, am.DEL_FLAG, am.district_id, am.address_id, am.channel_id, epu.epu_province, epu.epu_city, epu.epu_district, epu.epu_name, 'M0005' as epu_type, a.PROVINCE_NAME_CN AS epu_province_name, b.CITY_NAME_CN AS epu_city_name, c.CITY_NAME_CN AS epu_district_name FROM t_ammeter_info am LEFT JOIN t_epu_info epu ON am.epu_id = epu.row_id LEFT JOIN t_sy_provinces_info a ON epu.epu_province = a.PROVINCE_ID LEFT JOIN t_sy_city_info b ON epu.epu_city = b.CITY_CODE LEFT JOIN t_sy_city_info c ON epu.epu_district = c.CITY_CODE where 1=1 and am.DEL_FLAG='0' and epu.DEL_FLAG='0'
and am.row_id = #{rowId,jdbcType=VARCHAR}
and am.epu_Id = #{epuId,jdbcType=VARCHAR}
order by am.CREATE_TIME desc
